Clear-Host
Get-PhysicalDisk | sort EnclosureNumber,SlotNumber,DeviceId | select *,@{Name='SizeGB';Expression={'{0:0.0} GB' -f ($_.Size/(1024*1024*1024))}},@{Name='AllocatedSizeGB';Expression={'{0:0.0} GB' -f ($_.AllocatedSize/(1024*1024*1024))}} | ft DeviceId,FriendlyName,CanPool,Usage,SizeGB,AllocatedSizeGB,PhysicalLocation -auto
Get-StoragePool | sort FriendlyName | ? IsPrimordial -eq $false | select *,@{Name='SizeGB';Expression={'{0:0.0} GB' -f ($_.Size/(1024*1024*1024))}},@{Name='AllocatedSizeGB';Expression={'{0:0.0} GB' -f ($_.AllocatedSize/(1024*1024*1024))}} | ft FriendlyName,ProvisioningTypeDefault,ResiliencySettingNameDefault,SizeGB,AllocatedSizeGB -auto
Get-StoragePool | sort FriendlyName | ? IsPrimordial -eq $false -PipelineVariable sp | Get-ResiliencySetting | select @{Name='StoragePool';Expression={$sp.FriendlyName}},Name,NumberOfColumnsDefault,NumberOfColumnsMin,NumberOfColumnsMax,NumberOfDataCopiesDefault,NumberOfDataCopiesMin,NumberOfDataCopiesMax,InterleaveDefault,PhysicalDiskRedundancyDefault,PhysicalDiskRedundancyMin,PhysicalDiskRedundancyMax,RequestNoSinglePointOfFailure | ft *
Get-VirtualDisk | sort FriendlyName | select *,@{Name='SizeGB';Expression={'{0:0.0} GB' -f ($_.Size/(1024*1024*1024))}},@{Name='AllocatedSizeGB';Expression={'{0:0.0} GB' -f ($_.AllocatedSize/(1024*1024*1024))}},@{Name='FootprintOnPoolGB';Expression={'{0:0.0} GB' -f ($_.FootprintOnPool/(1024*1024*1024))}} | ft FriendlyName,ResiliencySettingName,SizeGB,AllocatedSizeGB,FootprintOnPoolGB -auto
Get-Disk | sort Number | select *,@{Name='SizeGB';Expression={'{0:0.0} GB' -f ($_.Size/(1024*1024*1024))}},@{Name='AllocatedSizeGB';Expression={'{0:0.0} GB' -f ($_.AllocatedSize/(1024*1024*1024))}} | ft Number,FriendlyName,SizeGB,AllocatedSizeGB,PartitionStyle -auto